<script>
/**
 * University of Cambridge, Information Services
 * Change Request #29 - Turnitin Assignments T&C Checkbox Solution
 *
 * Author: R. Al-Rawi (ra273)
 *
 * This is a JavaScript script that looks for specific pages in Moodle
 * as defined by a CSS selector, and injects HTML into the page at a
 * specific insertion point.
 * The HTML consists of a checkbox and text,
 */
 
console.log('** UniOfCam-UIS-ra273: tii-checkbox: initialising...');
 
/**
 *
 * Set up array of pages that trigger the checkbox creation
 *
 * Each page is a JavaScript object that defines:
 *
 *   - name:             the name of the page object
 *   - csspathpage:      the CSS path that identifies the page
 *   - csspathip:        the CSS path of the insertion point
 *   - csspathipelement: the CSS path of the insertion point element to insert before
 *   - buttonids:        an array of the ids of buttons to enable/disable
 *   - actions:          any actions (i.e. JavaScript functions) to call
 *
 */
 
/************************************************************************/
 
/** array to hold page objects */
var triggerPages = [];
 
/** page object for "new TII2 assignment" page */
var tpNewTIIAssignment = {
    name             : 'tpNewTIIAssignment',
    csspathpage      : '#page-mod-turnitintooltwo-mod:not([class*="cmid-"])',
    csspathip        : '#mform1 > fieldset.hidden',
    csspathipelement : '#mform1 > fieldset.hidden > div',
    buttonids        : ['id_submitbutton', 'id_submitbutton2'],
    actions          : []
};
 
triggerPages.push(tpNewTIIAssignment);
 
 
/** page object for "reset course" page */
var tpResetTIIAssignment = {
    name             : 'tpResetTIIAssignment',
    csspathpage      : '#page-course-reset',
    csspathip        : '#id_turnitintooltwoheader',
    csspathipelement : null,
    buttonids        : ['id_submitbutton'],
    actions          : ["toggleClass('id_turnitintooltwoheader', 'collapsed')"]
};
 
triggerPages.push(tpResetTIIAssignment);
 
/************************************************************************/
 
 
/**
 *
 * Function to toggle the css class for an HTML element.
 *
 * This either adds a class to a given element, if it's not
 * on the element already, or removes it, if it is present on the
 * element.
 *
 * elementId: id of the element to toggle the class on
 * className: name of the CSS class to toggle
 */
var toggleClass = function (elementId, className) {
 
    var classes = document.getElementById(elementId).classList;
 
    if (classes.contains(className)) {
        classes.remove(className);
    } else {
        classes.add(className);
    }
   
}
 
 
/**
 *
 * Function to enable/disable the "save" submit buttons.
 *
 * This function loops round an array of button element ids and
 * sets the "disabled" property for each element according to the
 * checked status of a given checkbox element.
 *
 * theCheckbox: the checkbox element object
 * triggerPage: a reference to the trigger page object
 */
var updateSubmitButtonState = function (theCheckbox, triggerPage) {
  /** Get the trigger page object */
  oTriggerPage = eval(triggerPage);
  console.log('** UniOfCam-UIS-ra273: tii-checkbox: ' + (theCheckbox.checked ? 'enabling' : 'disabling') + ' submit buttons...');
 
  /** loop round the buttonids array */
  oTriggerPage.buttonids.forEach(function (buttonId) {
 
    /** if we have a button id to work with */
    if (buttonId != '' && buttonId != null) {
      /** get a reference to the button */
      button = document.getElementById(buttonId);
      /**
       * set the button "disabled" property to the inverse
       * of the "checked" property for the passed-in checkbox.
       *
       * So when the checkbox is *checked*, the button is *enabled*
       */
      button.disabled = !theCheckbox.checked;
    }
 
  });
 
}
 
/**
 *
 * Function to create the checkbox + label, all inside a container div
 *
 */
var createCBDiv = function (triggerPage) {
    var policyStatement = 'I have read and understood the <a href="http://www.admin.cam.ac.uk/univ/plagiarism/examiners/detection.html" target="_blank">University\'s Conditions of Use</a> for Turnitin UK software';
 
    /** Create a div DOM element to contain the checkbox + label */
    var theDiv = document.createElement("div");
    theDiv.setAttribute("id", "tii_tandc");
    theDiv.setAttribute("class", "tii_tandc");
 
    /** HTML for checkbox and label */
    divInnerHTML = '<input onchange="updateSubmitButtonState(this, ' + triggerPage + ')" id="tii_tandc_cb" type="checkbox" class="felement">';
    divInnerHTML += '<label for="tii_tandc_cb">' + policyStatement + '</label>';
    theDiv.innerHTML = divInnerHTML;
 
    return theDiv;
}
 
/**
 *
 * Main Section
 *
 */
 
/** loop round the trigger pages defined */
triggerPages.forEach(function (tp) {
 
  console.log("** UniOfCam-UIS-ra273 - checking trigger page: " + tp.csspathpage + "...");
 
  /** can we find a match with the css path for this trigger page? */
  var foundPage = !(document.querySelector(tp.csspathpage) == null);
 
  /** Not on a trigger page - exit */
  if (foundPage == false) {
      console.log("** UniOfCam-UIS-ra273 - not on a trigger page - exiting... ");
      return;
  }
 
  console.log("** UniOfCam-UIS-ra273 - found trigger page... ");
 
  /** attempt to find the element matching the insertion point CSS path */
  var ip = document.querySelector(tp.csspathip);
 
  /** No insertion point found - exit */
  if (ip == null) {
      console.log("** UniOfCam-UIS-ra273 - insertion point not found - exiting... ");
      return;
  }
 
  console.log("** UniOfCam-UIS-ra273 - found insertion point... ");
 
  /** construct the checkbox div */
  var cbContainer = createCBDiv(tp.name);
  /**
   * insert the checkbox div into the insertion point
   * NOTE: using "insertBefore" here with an empty 2nd parameter inserts the
   * checkbox div as the last child element of the insertion point
   */
  ip.insertBefore(cbContainer, document.querySelector(tp.csspathipelement));
 
  /** loop round the array of buttons for the current trigger page and disable them */
  tp.buttonids.forEach(function (buttonId) {
 
    if (buttonId != '' && buttonId != null) {
        console.log("** UniOfCam-UIS-ra273 - disabling button" + buttonId);
        button = document.getElementById(buttonId);
        button.disabled = true;
    }
     
  });
 
  console.log("** UniOfCam-UIS-ra273 - there are " + tp.actions.length + " actions to run for this trigger page");
 
  /** loop round the array of actions to carry out at the end */
  tp.actions.forEach(function (action) {
 
    /** if we have a usable action, execute it */
    if (action != '' && action != null) {
        console.log("** UniOfCam-UIS-ra273 - executing action " + action);
        var f = new Function(action);
        return (f());
    }
     
  });
 
});
</script>    </footer>
